mapper.select:
SELECT * FROM (SELECT ROWNUM R, D.* FROM (SELECT * FROM TABLE_BOARD_freeTalk ORDER BY
BOARD_DATE DESC
BOARD_VIEW DESC
) D) B WHERE B.R BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE TABLE_BOARD_freeTalk 
SET BOARD_VIEW = BOARD_VIEW + 1 
WHERE BOARD_NUM = #{board_Num}

UPDATE TABLE_BOARD_freeTalk 
SET BOARD_TITLE = #{board_Title}, BOARD_CONTENT = #{board_Content} 
WHERE BOARD_NUM = #{board_Num}

UPDATE TABLE_REPLY_freeTalk
SET REPLY_CONTENT = #{reply_Content}
WHERE REPLY_NUM = #{reply_Num}

UPDATE TABLE_BOARD_freeTalk 
SET BOARD_LIKES = BOARD_LIKES + 1 
WHERE BOARD_NUM = #{board_Num}
